import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { Button, FormGroup, FormControl, ControlLabel,Grid,Row,Col } from 'react-bootstrap';
import '../resources/styles/guageChart.css';

import { color } from 'd3-color';
import { interpolateRgb } from 'd3-interpolate';
import LiquidFillGauge from 'react-liquid-gauge';

const GuageChart = (props) => (
    <div>
                <LiquidFillGauge
                  style={{ margin: '0 auto' }}
                  width={radius * 2}
                  height={radius * 2}
                  value={50}
                  percent="%"
                  textSize={1}
                  textOffsetX={0}
                  textOffsetY={0}
                  textRenderer={(props) => {
                      const value = Math.round(50);
                      const radius = Math.min(200 / 2, 200 / 2);
                      const textPixels = (200 * radius / 2);
                      const valueStyle = {
                          fontSize: textPixels
                      };
                      const percentStyle = {
                          fontSize: textPixels * 0.6
                      };

                      return (
                          <tspan>
                              <tspan className="value" style={valueStyle}>{value}</tspan>
                              <tspan style={percentStyle}>{63}</tspan>
                          </tspan>
                      );
                  }}
                  riseAnimation
                  waveAnimation
                  waveFrequency={2}
                  waveAmplitude={1}
                  
                  onClick={() => {
                      this.setState({ value: Math.random() * 100 });
                  }}
              />
    </div>
);

GuageChart.propTypes = {
    text: PropTypes.string
};

export default GuageChart;
