import React from 'react';
import { AwesomeButton } from 'react-awesome-button';
//import { Input } from 'react-bootstrap';

import 'react-awesome-button/dist/styles.css';
import 'react-awesome-button/dist/themes/theme-blue.css';
import { Container, Row, Col, Input, Button, Fa, Card, CardBody, ModalFooter } from 'mdbreact';
//import './bg1.jpg';
import { createBrowserHistory } from 'history';
// import Header from '../Header';
// import Footer from '../Footer';
import './Login.css';

const history = createBrowserHistory({
  hashType: 'slash' // Omit the leading slash
});

class FormsPage extends React.Component  {

  constructor(props) {
    super(props);

    this.state ={
      username: '',
      password:'',
      checked:false,
      acceptedCondition: false

    };
    this.handleChangeUserName = this.handleChangeUserName.bind(this);
    this.handleChecked = this.handleChecked.bind(this);
    this.handleChangePassword = this.handleChangePassword.bind(this);
    this.submit = this.submit.bind(this);
    //this.handleSubmit = this.handleSubmit.bind(this)
  }
//   handleInput(e) {
//     if(!e.target.checked)
//        this.setState({ usernsme: '' ,password:''});
//  }



  searchKeyPress=(e)=>
  {
      console.log(e.which);
      e=e || window.event;
      if(e.which === 13)
      {
        this.submit();
      }
  }
  handleChangeUserName(event) {
    this.setState({
      username: event.target.value
    });
  }

  handleChangePassword(event) {
    this.setState({
      password: event.target.value,
    });
  }
  handleChecked(event) {
    this.setState({
      checked: event.target.checked
    });
  }

  submit() {
    // Integrating real API
    let param = [{
      "username": this.state.username,
      "password": this.state.password
    }];
  }

  getErrorMessageOnNotAccepting() {
    console.log('called');
    if(this.state.acceptedCondition) {
      if(this.state.checked) {
        return null;
      } else {
        return(
        <div style={{color: 'red',marginTop:-30, justifyContent:'center'}}>
          Please accept the Terms and Conditions
        </div>
      );
      }
    } else {
      if(this.state.checked) {
        return null;
      }
    }
  }

  render() {

    const url = window.location.href;
    //starting on the 8th character after "http://" or "https://", find '/'
    const startChar = url.indexOf('/', 8);
    const path = url.substr(startChar, url.length);
    console.log("path=" + path);
    console.log(this.state.checked)

    return(
      <div>
        {/* <Header path={path}/> */}
          <div id="abc" style={{backgroundSize:'cover'}}>
            <Container id="container1">
            <div style={{display:'flex',flexDirection:'Row',justifyContent:'center'}}>
            <img src="https://xchange.ipc.com/_layouts/IPC.CP2012/images/login-page-new.jpg" height="525" style={{width:450}} />
              <section className="form-light">
                <Row>

                <form onKeyPress={this.searchKeyPress}>
                  <Col md="5">

                    <Card className=" card card-image" style={{width: '24rem',height:'20rem',borderBottomRightRadius:50}}>
                      <div className="text-white rgba-stylish-strong py-3 px-5 z-depth-4" style={{backgroundColor: 'white',borderBottomRightRadius:50}}>
                        <div className="text-center"  style={{display: 'flex', flexDirection: 'row', justifyContent: 'left'}} >
                        <img src="https://xchange.ipc.com/_layouts/IPC.CP2012/images/ipc-footer-logo.png" height="50" style={{marginTop:30,marginRight:10}} />
                          <h5 style={{display: 'flex', flexDirection: 'row',paddingTop:20 }}className="black-text mb-5 mt-4 font-weight-bold"><strong><i>Onboarding </i></strong> <a className="black-text font-weight-bold"><strong>   Application</strong></a></h5>
                        </div>
                        <Input className="text-grey" group type="text" label="Enter your username" value={this.state.username} onChange={this.handleChangeUserName}  validate autocomplete="off" />
                        <Input className="text-grey" label="Enter your password" value={this.state.password} onChange={this.handleChangePassword} group type="password" validate autocomplete="off"/>

                         <div className="form-check my-4" style={{marginLeft:20}}>
                      <input className="form-check-input"style={{height:15,width:25,marginTop:10,marginLeft:-20}} type="checkbox" value={this.state.checked} id="defaultCheck17" onChange={(e) => this.handleChecked(e)}/>
                      <label className="form-check-label black-text"style={{marginTop:6}} htmlFor="defaultCheck17">I agree to<a href="#" className="pink-text font-weight-bold"> IPC's Terms and Conditions</a></label>
                    </div>







                        <Row className="d-flex align-items-center mb-4">
                          <div className="text-center mb-3 col-md-6">
                          {/* <AwesomeButton type="primary" action={() => this.submit()} style={{ width: 110, height: 50}}>Sign In</AwesomeButton> */}
                            <Button color="" rounded type="button" className="grad-button btn-block z-depth-1" style={{marginLeft:90,width:'100px'}} onClick={this.submit}>Sign in</Button>
                          </div>

                        {/* <Col md="10"> */}
                        {/* <br/>
                        <br/> */}
                        {this.getErrorMessageOnNotAccepting()}
                        <p className="font-small pink-text" style={{paddingLeft:70}}><a href="#" className="black-text ml-1 font-weight-bold">Forgot Username/Password</a></p>
                        {/* <br/> */}
                        <a href="/new_user" className="pink-text ml-1 font-weight-bold" style={{paddingLeft:70}}>New User Registration</a>

                            {/* </Col> */}
                        </Row>

                      </div>
                    </Card>

                  </Col>
                  </form>
                </Row>
              </section>
              </div>

          {/* <div style={{display: 'flex', flexDirection: 'row', justifyContent: 'center'}}><p style={{color:'white',paddingTop:10,textAlign:'center',position:'fixed'}}>Copyright IPC Systems,Inc,All Rights Reserved</p></div> */}
          <div style={{paddingTop: 70 ,display: 'flex', flexDirection: 'row', justifyContent: 'center'}}>
          <p style={{color:'white',paddingTop:10,textAlign:'center'}}>Copyright IPC Systems,Inc,All Rights Reserved</p>
          </div>
            </Container>

          </div>

        {/* <Footer/>  */}
      </div>
    );
  }
};

export default FormsPage;
