    }


    axios.post('http://localhost:3001/api/v1/adduser',this.state).then(function(res){
      console.log('res', res.data);
      if(res.data.alreadyExists)
      {
        //user already exists
        notify.show("User already exists.", "error", 2000);
      }
      else if (res.data.createdSuccessFully) {
        //created successfully
        notify.show("User added successfully.", "success", 2000);
      }
      else {
        //system error
        notify.show("System error, Please try after some time.", "error", 2000);
      }
    }).catch(function(error) {
      console.log('error',error);
    })

    console.log(this.state);
    //set state to Initial state
    this.setState({modalIsOpen: false,
    firstName:'',
    lastName:'',
    emailId : '',
    contactNumber:'',
    employeeId:'',
    password:'',
    confirmPassword:'',
    isAdmin:false});
  }

  addUser(event)
  {

    this.setState({modalIsOpen: true});
    console.log(event);
  }

  openModal() {
    this.setState({modalIsOpen: true});
  }

  afterOpenModal() {
    // references are now sync'd and can be accessed.
    //this.subtitle.style.color = '#f00';https://www.youtube.com/watch?v=MTsFmUI54as
  }

  closeModal() {
    this.setState({modalIsOpen: false});
  }


  render() {
    return (
        <div className="manageUsers">
          <div className="userStates">
              <Label text="Total Users- 100&nbsp;&nbsp;|&nbsp;&nbsp;Active Users- 88"></Label>
          </div>
          <div className="users">
            <div className="addUsers"><RButton text={"Add User"} onClickImplementation={this.addUser}></RButton></div>
            <div className="searchUsers"><TextBox inputType = "text" placeholder="Search"></TextBox><img src={require('./../../resources/images/search.png')} style={{width:'20px',height:'20px', cursor:'pointer'}} name="report"/></div>
          </div>
          <RGrid
            columnDefs={[
                { key: 'id', name: 'ID' },
                { key: 'name', name: 'Name' },
                { key: 'mail', name: 'Email ID' },
                { key: 'contact', name: 'Contact Number' },
                { key: 'isActive', name: 'Status' },
                { key: 'action', name: 'Action' }
              ]}
            rowData = {[
              {id: 1000, name: 'Kim', mail: 'kim@r1.com',contact: '+65 - 99XX99XX99', isActive:'Active', action:'De-Activate'},
              {id: 1000, name: 'Kim', mail: 'kim@r1.com',contact: '+65 - 99XX99XX99', isActive:'Active', action:'De-Activate'},
              {id: 1001, name: 'Kim', mail: 'kim@r1.com',contact: '+65 - 99XX99XX99', isActive:'Active', action:'De-Activate'},
              {id: 1002, name: 'Kim', mail: 'kim@r1.com',contact: '+65 - 99XX99XX99', isActive:'Active', action:'De-Activate'},
