              {id: 1034, name: 'Kim', mail: 'kim@r1.com',contact: '+65 - 99XX99XX99', isActive:'Active', action:'De-Activate'}]}
              >
          </RGrid>
          {/*
            <div className="users">
              <Label text={"Active Users : 100, In-Active Users : 50"}></Label>
            </div>

            className={{ base: [style.base]}}
            overlayClassName={{ base: [style.overlayBase] }}
            style={customStyles}
            */}
            <Modal
              isOpen = {this.state.modalIsOpen}
              onAfterOpen = {this.afterOpenModal}
              onRequestClose = {this.closeModal}
              className = "Modal"
              overlayClassName = "Overlay"
              ariaHideApp = {false}
            >
            <div className="createUser">

              <div className ="flexRow">
                  <div className="flexColumn">
                      <Label text={"First Name"} color={"#fff"} size={"12px"}></Label>
                  </div>
                  <div className="flexColumn">
                      <TextBox inputType="text" width={"250px"} name="firstName" onChangeEvent={this.changeTaxtchange}></TextBox>
                  </div>
              </div>
              <div className ="flexRow">
                  <div className="flexColumn">
                      <Label text={"Last Name"} color={"#fff"} size={"12px"}></Label>
                  </div>
                  <div className="flexColumn">
                      <TextBox inputType="text" width={"250px"} name="lastName" onChangeEvent={this.changeTaxtchange}></TextBox>
                  </div>
              </div>
              <div className ="flexRow">
                  <div className="flexColumn">
                      <Label text={"Email ID"} color={"#fff"} size={"12px"}></Label>
