import React, { Component } from 'react';
import {Button, FormGroup, FormControl, ControlLabel, Grid, Row, Col, Tooltip, OverlayTrigger} from 'react-bootstrap';
import './../../resources/styles/style.css';
import './Report.css';
import Label from './../../components/Label.js';
import TextBox from './../../components/TextBox.js';
import {Animated} from "react-animated-css";
import Iframe from 'react-iframe';
import Header from './../../components/Header.js';
import Config from '../Config/Config.js';
import SmoothieComponent from 'react-smoothie';
import axios from 'axios';
import Chart from 'react-google-charts';
import Loader from 'react-loader-spinner';
import API from './../../Config/api.json';
// import {PieChart} from 'react-easy-chart';
import {PieChart, BarChart, Legend, ResponsiveContainer} from 'react-easy-chart';
import ReactSvgPieChart from "react-svg-piechart"


class Analytics extends Component {
  constructor(props) {
    super(props);

    this.state = {logLevel:[], loader:false, reportCalculatedData:[]};
  }

  componentDidMount() {

      axios.get(API.current.apiUrl+'/api/v1/cpuinfo').then(function(res){
        console.log(res.data.cpu);

      }).catch(function(err){
        console.log(err);
      })

      let thisObj = this;

      this.setState({logLevel:[
        {title: "Emergency (70)", value: 70, color: "#ef0000"},
        {title: "Alert (60)", value: 60, color: "#A94442"},
        {title: "Critical (30)", value: 30, color: "#FFA500"},
        {title: "Error (20)", value: 20, color: "#ffa578"},
        {title: "Warning (18)", value: 18, color: "#ff78a9"},
        {title: "Notice (97)", value: 97, color: "#1911bf"},
        {title: "Informational (112)", value: 112, color: "#10bf67"},
        {title: "Debug (101)", value: 101, color: "#00e822"},
      ]})
      let row = {'emergency':70, 'alert': 60, 'critical':30, 'error': 20, 'warning':18, 'notice':97, 'informational':112, 'debug':101};
      let countAll = 508;
      let reportRowCalculatedData = [
        {x: "Emergency" + " ("+row.emergency +" | "+ ((parseInt(row.emergency)/countAll)*100).toFixed(2)+"%)"},
        {x: "Alert" + " ("+row.alert+" | " + ((parseInt(row.alert)/countAll)*100).toFixed(2)+"%)"},
        {x: "Critical" + " ("+row.critical+" | "+((parseInt(row.critical)/countAll)*100).toFixed(2)+"%)"},
        {x: "Error" + " ("+row.error+" | " +((parseInt(row.error)/countAll)*100).toFixed(2)+"%)"},
        {x: "Warning" + " ("+row.warning+ " | "+((parseInt(row.warning)/countAll)*100).toFixed(2)+"%)"},
        {x: "Notice" + " ("+row.notice+" | "+((parseInt(row.notice)/countAll)*100).toFixed(2)+"%)"},
        {x: "Informational" + " ("+row.informational+ " | "+((parseInt(row.informational)/countAll)*100).toFixed(2)+"%)"},
        {x: "Debug" + " ("+row.debug+" | "+((parseInt(row.debug)/countAll)*100).toFixed(2)+"%)"}
      ]

      this.setState({reportCalculatedData:reportRowCalculatedData});

      var ts1 = this.refs.chart.addTimeSeries({
        strokeStyle: 'rgba(0, 255, 0, 1)',
        fillStyle: 'rgba(0, 255, 0, 0.1)',
        lineWidth: 1,
      });

      this.dataGenerator = setInterval(function() {
        var time = new Date().getTime();
        // Generate times slightly in the future
        time += 1000;
        axios.get(API.current.apiUrl+'/api/v1/cpuinfo').then(function(res){
          console.log(res.data.cpu);

          ts1.append(time, res.data.cpu);
          //ts2.append(time, Math.random());
        }).catch(function(err){
          console.log(err);
        })

      }, 1000);

  }

  componentWillUnmount()
  {
    clearInterval(this.dataGenerator);
  }

  render() {
    return (
        <div>
            <Grid>
                  <Row className="logStatesDiv">
                      {/*<Col xs={12} md={6}>
                          <Label text={"CPU States"} color="#000"></Label>
                      </Col>*/}

                      <Col xs={12} md={12}  style={{padding:'5px'}}>
                            <SmoothieComponent ref="chart" responsive={true} interpolation="smooth" minValue={0}  maxValue={100} strokeStyle="#ffffff"/>

                                <Label weight="bold" text={"CPU States"} color="#000" size="10px"></Label>
                      </Col>
                  </Row>


                  <Row className="logStatesDiv">
                      <Col xs={12} md={6}>
                        <Legend data={this.state.reportCalculatedData} dataId={'x'} horizontal
                          config= {
                            [
                              {color: '#ef0000'},{color: '#a94442'},{color: '#FFA500'},
                              {color: '#ffa578'},{color: '#ff78a9'},{color: '#1911bf'},
                              {color: '#10bf67'},{color: '#00e822'}
                            ]
                          }/>
                      </Col>

                      <Col xs={12} md={6}  style={{padding:'5px', textAlign:'center'}}>
                              <div style={{width:'200px', height:'200px', margin:'0 auto'}}>
                                <ReactSvgPieChart
                                    expandOnHover={true}
                                    expandSize={5}
                                    shrinkOnTouchEnd={false}
                                    strokeColor="#fff"
                                    strokeLinejoin="miter"
                                    strokeWidth={1}
                                    viewBoxSize={100}
                                    data = {this.state.logLevel}
                                    transitionDuration={"1s"}
                                  />
                              </div>
                            <Label weight="bold" text={"Error States For Last 7 Days"} color="#000" size="10px"></Label>
                      </Col>
                  </Row>
            </Grid>
            {(this.state.loader)?
                <div className="fadeMe">
                    <div style={{position:'absolute', left:'45%', top: '40%'}}>
                      <Loader
                         type="ThreeDots"
                         color="#00BFFF"
                         height="150"
                         width="150"
                      />
                    </div>
                </div>:<div></div>
            }
        </div>
    );
  }
}

export default Analytics;
