import React, { Component } from 'react';
import {Button, FormGroup, FormControl, ControlLabel, Grid, Row, Col, Tooltip, OverlayTrigger} from 'react-bootstrap';
import './../../resources/styles/style.css';
import './Report.css';
import Label from './../../components/Label.js';
import TextBox from './../../components/TextBox.js';
import {Animated} from "react-animated-css";
import Iframe from 'react-iframe';
import Header from './../../components/Header.js';
import Config from '../Config/Config.js';
import SmoothieComponent from 'react-smoothie';
import axios from 'axios';
import Chart from 'react-google-charts';
import Loader from 'react-loader-spinner';
import API from './../../Config/api.json';
// import {PieChart} from 'react-easy-chart';
import {PieChart, BarChart, Legend, ResponsiveContainer} from 'react-easy-chart';
import ReactSvgPieChart from "react-svg-piechart";
import Meter from '../../components/meter.js';
import Datetime  from 'react-datetime';
import Modal from 'react-responsive-modal';
import Pagination from "react-js-pagination";

class Analytics extends Component {
  constructor(props) {
    super(props);
    this.state = {loader:false, logInformation:[],logModelOpen:false,applianceHostName:'',appliancesLogs:[], activePage:1,itemsCountPerPage:0,totalItemsCount:0,pageRangeDisplayed:0};
    //this.bindLoglevels = this.bindLoglevels.bind(this);
    this.onApplicanceClick = this.onApplicanceClick.bind(this);
    this.onCloseModal = this.onCloseModal.bind(this);
    this.onPageChange = this.onPageChange.bind(this);
  }

  componentDidMount() {
    this.bindLoglevels();
  }

  componentWillUnmount()
  {

  }

  onPageChange(pageNumber)
  {
    //console.log(event);
    this.setState({activePage:pageNumber});
  }

  bindLoglevels()
  {
    let pageObj = this;
    axios.post(API.current.apiUrl+"/api/v1/analytics",{}).then(function(res)
    {
        pageObj.setState({logInformation:res.data.data});

    }).catch(function(err){
        console.log(err);
    })
  }

  getLogs(appliance)
  {
    let pageObj = this;
    this.setState({appliancesLogs:[]});
    axios.post(API.current.apiUrl+"/api/v1/logs",{}).then(function(res)
    {
        console.log(res.data.hits.hits);
        pageObj.setState({appliancesLogs:res.data.hits.hits});
    }).catch(function(err){
        console.log(err);
    })
  }
  onApplicanceClick(event)
  {
    console.log(event.target.name);
    console.log(event.target.value);
    this.setState({logModelOpen:true,applianceHostName:event.target.name});
    this.getLogs("");
  }

  onCloseModal()
  {
    this.setState({logModelOpen:false});
  }


  render() {
    return (
        <div style={{'height':'85vh', 'overflowY':'scroll'}}>
          <Grid className="override_container">
              <Row className="show-grid">
                <Col xs={12} md={2}>
                  <Label text={"Start Date"} color={"#000"} size={"12px"}></Label>
                </Col>
                <Col xs={12} md={3}>
                      <Datetime className="datePickerField" value={this.state.startDate} onChange={this.onStartDateChange}/>
                </Col>
                <Col xs={12} md={2}>
                  <Label text={"End Date"} color={"#000"} size={"12px"}></Label>
                </Col>
                <Col xs={12} md={3}>
                      <Datetime className="datePickerField" value={this.state.endDate} defaultValue={new Date()} onChange={this.onEndDateChange}/>
                </Col>
                <Col xs={12} md={2}>
                  <Button bsStyle="primary" className="button userPagebtn" bsSize="small" onClick={this.onGenerateReportsClick}>
                    Apply
                  </Button>
                </Col>
              </Row>
            </Grid>

            <div>
              <div>
                    {this.state.logInformation.map((value, index) => {
                      return <Meter logLevelDescription={value.logLevelDescription} totalPackets={value.totalPackets} packetsForLogLevel={value.packetsForLogLevel} key={index}
                        topApplicances={value.topApplicances.SYSLOG_HOSTNAME.buckets} logLevel={value.logLevel} onApplicanceClick={this.onApplicanceClick}></Meter>
                  })}
              </div>
            </div>

            <Modal open={this.state.logModelOpen} onClose={this.onCloseModal} center>
              <Grid style={{width:'100%', height:'90vh'}}>
                  <Row className="show-grid">
                      <Col xs={12} md={12}>
                        <div style={{textAlign :'center'}}><b style={{fontFamily:'Times New Roman, Times, serif', textDecoration:'none', fontSize:'16px', color:'#000'}}>Showing Logs for:  {this.state.applianceHostName}</b></div>
                            <div style={{backgroundColor:'#222222', padding:'2px', height:'80vh', overflowY:'scroll'}}>
                                  <ul>
                                      {this.state.appliancesLogs.map((value, index)=>{
                                          return <li type="none" style={{fontSize:'10px', color:'#ffffff'}}  key={index}>{value._source.message}</li>
                                      })}
                                  </ul>
                            </div>
                      </Col>
                  </Row>
                  <Row className="show-grid">
                    <Col xs={12} md={12}>
                      <Pagination
                          activePage={this.state.activePage}
                          itemsCountPerPage={1000}
                          totalItemsCount={this.state.totalItemsCount}
                          pageRangeDisplayed={15}
                          onChange={this.onPageChange}
                        />
                    </Col>
                  </Row>
              </Grid>
            </Modal>
        </div>
    );
  }
}

export default Analytics;
