import React, { Component } from 'react';
import {Button, FormGroup, FormControl, ControlLabel, Grid, Row, Col, Tooltip, OverlayTrigger} from 'react-bootstrap';
import './../../resources/styles/style.css';
import './Report.css';
import Label from './../../components/Label.js';
import TextBox from './../../components/TextBox.js';
import {Animated} from "react-animated-css";
import Iframe from 'react-iframe';
import Header from './../../components/Header.js';
import ShowReports from './ShowReports.js';
import Analytics from './Analytics.js';
import Textarea from 'react-textarea-autosize';
import axios from 'axios';

class ManageReport extends Component {
  constructor(props) {
    super(props);
    this.state =
    {
      isEditMode:false,
      retainDataMaxDays:0,
      retainLogsMaxDays:0,
      reportReciepents:''
    };

    this.changeTaxtchange = this.changeTaxtchange.bind(this);
    this.updateConfig = this.updateConfig.bind(this);
  }

  updateConfig(event)
  {
    console.log(event);

  }

  changeTaxtchange(event)
  {
    console.log('event', event.target.name);

    if(event.target.name == "purgeDataDays")
    {
      this.setState({retainDataMaxDays:event.target.content});
    }
    else if(event.target.name == "purgeLogsDays")
    {
      this.setState({retainLogsMaxDays:event.target.content});
    }
    else {
      this.setState({reportReciepents:event.target.value});
    }

  }

  bindConfigDetails()
  {
    let pageObj = this;
    console.log('login');
    axios.get('http://localhost:3001/api/v1/config').then(function(resp){
        if(resp.data.success)
        {
            pageObj.setState({
              isEditMode:true,
              retainDataMaxDays:resp.data.data[0].retain_data_max_days,
              retainLogsMaxDays:resp.data.data[0].retain_logs_max_days,
              reportReciepents:resp.data.data[0].report_receipients_colon_separated
            });
        }
    }).catch(function(err){
      console.log('err', err);
    })
  }

  componentDidMount()
  {
    this.setState({userName:(sessionStorage.getItem('user'))?sessionStorage.getItem('user'):''});
    this.bindConfigDetails();

  }

  render() {
    return (
        <div className="manageShowReports" style={{color:'#000'}}>

            <div className="userStates">
                <Label text={"Daily Report Count(s)- 100  | Weekly Report(s) - NA | Monthly Report(s) - NA | Custom Report(s) - NA"}></Label>
            </div>

            <div className="createUser" style={{margin:'5px', marginTop:'10vh'}}>

                <Grid className="override_container">

                    <Row className="show-grid">
                      <Col xs={12} md={3}>
                        <Label text={"Purge Data After (Days)"} color={"#fff"} size={"12px"}></Label>
                      </Col>
                      <Col xs={12} md={3}>
                        <TextBox inputType="number" width={"250px"} name="purgeDataDays" content={this.state.retainDataMaxDays} onChangeEvent={this.changeTaxtchange}></TextBox>
                      </Col>
                      <Col xs={12} md={3}>
                        <Label text={"Purge Logs After (Days)"} color={"#fff"} size={"12px"}></Label>
                      </Col>
                      <Col xs={12} md={3}>
                        <TextBox inputType="number" width={"250px"} name="purgeLogsDays" content={this.state.retainLogsMaxDays} onChangeEvent={this.changeTaxtchange}></TextBox>
                      </Col>
                    </Row>

                    <Row className="show-grid">
                      <Col xs={12} md={3}>
                        <Label text={"Report Receipients ( ; separated)"} color={"#fff"} size={"12px"}></Label>
                      </Col>
                      <Col xs={12} md={9}>
                        <Textarea maxRows={15} minRows={1} className="tb ta" name="reportEmailIds" value={this.state.reportReciepents} onChange={this.changeTaxtchange}></Textarea>
                      </Col>
                    </Row>

                    <Row className="show-grid">
                      <Col xs={12} md={3}>
                        <Button bsStyle="primary" className="button userPagebtn" bsSize="small" onClick={this.adduserToDB} style={{display:(this.state.isEditMode) ? "none" : "" }}>
                          Submit
                        </Button>
                        <Button bsStyle="primary" className="button userPagebtn" bsSize="small" onClick={this.updateConfig} style={{display:(!this.state.isEditMode) ? "none" : "" }}>
                          Update
                        </Button>
                        <Button bsStyle="primary" className="button userPagebtn" bsSize="small" onClick={this.onExit}>
                          Cancel
                        </Button>
                      </Col>
                      <Col xs={12} md={3}>
                        <div></div>
                      </Col>
                      <Col xs={12} md={3}>
                        <div></div>
                      </Col>
                      <Col xs={12} md={3}>
                        <div></div>
                      </Col>
                    </Row>
                </Grid>

              </div>
        </div>
    );
  }
}

export default ManageReport;
