import React, { Component } from 'react';
import { Button, FormGroup, FormControl, ControlLabel,Grid,Row,Col } from 'react-bootstrap';
import './resources/styles/landing.css';
import {Animated} from "react-animated-css";
import TextBox from './components/TextBox.js';
import Label from './components/Label.js';
import Notifications, {notify} from 'react-notify-toast';
import axios from 'axios';
import ReactCardFlip from 'react-card-flip';
import Loader from 'react-loader-spinner';
import Particles from 'react-particles-js';
import Footer from './components/Footer.js';
import API  from './Config/api.json';
class Landing extends Component {

  constructor(props) {
    super(props);
    this.state = {email_id:'', password:'', isFlipped: false, loader:false};
    this.onChangeEvent = this.onChangeEvent.bind(this);
    this.onLoginButtonClick = this.onLoginButtonClick.bind(this);
    this.onForgotPasswordClick = this.onForgotPasswordClick.bind(this);
  }

  onForgotPasswordClick(event)
  {
    notify.show("Not Implemented yet.", "error", 2000);
  }

  onLoginButtonClick(event)
  {
      console.log(API);
    if((this.state.email_id === "")&&(this.state.password === ""))
    {
      notify.show("Please enter User Name and Password.", "error", 2000);
    }
    else
    {
        let pageObj = this;
        this.setState({loader:true});

        axios.post('http://localhost:3001/api/v1/login',{email_id:this.state.email_id, password:this.state.password}).then(function(res){
          console.log('res',res.data.isUser);
          pageObj.setState({loader:false});
          if(!res.data.isUser)
          {
            notify.show("User Name or Password is wrong.", "error", 2000);
            return false;
          }
          else
          {
            console.log('res', res.data);
            sessionStorage.setItem('user', res.data.userName);
            sessionStorage.setItem('admin', res.data.isAdmin);
            pageObj.props.history.push('/dashboard')
          }

        }).catch(function(error) {
          console.log('error',error);
          pageObj.setState({loader:false});
        })
    }
  }

  onChangeEvent(event)
  {
      if(event.target.name === "email_id")
      {
        this.setState({ email_id: event.target.value},() => console.log(this.state));
      }
      else {
        this.setState({ password: event.target.value},() => console.log(this.state));
      }
  }

  timer(){
    this.setState(prevState => ({ isFlipped: !prevState.isFlipped }));
  }

  componentDidMount() {
    this.intervalId = setInterval(this.timer.bind(this), 2000);
  }

  componentWillUnmount(){
    clearInterval(this.intervalId);
  }

  render() {
    return (
        <div className="login" >
          <div className="formGrid">
            <Grid>
              <Row className="show-grid">
                <Col xs={12} md={6} className="rightBorder">
                    {/*grid for login fields*/}
                    <form>
                      <Grid>
                        <Row className="show-grid" style={{padding:'5px'}}>
                          <Col xs={12} md={4}>
                            <Label text="User Name"/>
                          </Col>
                          <Col xs={12} md={8}>
                            <TextBox onChangeEvent={this.onChangeEvent} name="email_id" inputType="text" width="100%"/>
                          </Col>
                        </Row>

                        <Row className="show-grid" style={{padding:'5px'}}>
                          <Col xs={12} md={4}>
                            <Label text="Password"/>
                          </Col>
                          <Col xs={12} md={8}>
                            <TextBox onChangeEvent={this.onChangeEvent} name="password" inputType="password" width="100%"/>
                          </Col>
                        </Row>

                        <Row className="show-grid" style={{padding:'5px'}}>
                          <Col xs={12} md={4}>
                            <div></div>
                          </Col>
                          <Col xs={12} md={8}>
                            <Button bsStyle="primary" className="button" bsSize="small" onClick={this.onLoginButtonClick}>
                              Login
                            </Button>
                          </Col>
                        </Row>

                        <Row className="show-grid" style={{padding:'5px'}}>
                          <Col xs={12} md={4}>
                            <div></div>
                          </Col>
                          <Col xs={12} md={8}>
                            <Label text="Forgot Password" cursor={"pointer"} onLabelClick={this.onForgotPasswordClick}/>
                          </Col>
                        </Row>

                      </Grid>
                    </form>
                    {/*grid for login fields ends*/}
                </Col>
                <Col xs={12} md={12} className="horizontalLine"><hr></hr></Col>
                <Col xs={12} md={6}>
                  {/*div for logo flipper*/}
                    <Grid>
                      <Row className="show-grid" style={{padding:'5px'}}>
                        <Col xs={12} md={6}>
                          <div></div>
                        </Col>
                        <Col xs={12} md={6}>
                          <div></div>
                        </Col>
                      </Row>

                      <Row className="show-grid card" style={{padding:'5px'}}>
                        <Col xs={12} md={12}>
                          <ReactCardFlip isFlipped={this.state.isFlipped} infinite={true}>
                            <div key="front"><img src={require('./resources/images/sing_logo_login.png')} style={{width:'175px', height:'40px'}}></img></div>
                            {/*<div key="front"><img src={require('./resources/images/Remego logo.jpg')} style={{width:'200px', height:'50px'}}></img></div>*/}
                            <div key="back"><img src={require('./resources/images/sing_logo_login.png')} style={{width:'175px', height:'40px'}}></img></div>
                          </ReactCardFlip>
                        </Col>
                      </Row>

                      <Row className="show-grid" style={{padding:'5px'}}>
                        <Col xs={12} md={6}>
                          <div></div>
                        </Col>
                        <Col xs={12} md={6}>
                          <div></div>
                        </Col>
                      </Row>
                    </Grid>
                  {/*div for logo flipper ends*/}
                </Col>
              </Row>
            </Grid>
          </div>

          {/*<Footer></Footer>*/}

          <Notifications options={{zIndex: 200, top: '50px'}} />
            {(this.state.loader)?
                <div className="fadeMe">
                    <div style={{position:'absolute', left:'45%', top: '40%'}}>
                      <Loader
                         type="ThreeDots"
                         color="#00BFFF"
                         height="150"
                         width="150"
                      />
                    </div>
                </div>:<div></div>
            }
        </div>
    );
  }
}

export default Landing;
