import React, { Component } from 'react';
import {Button, FormGroup, FormControl, ControlLabel, Grid, Row, Col, Tooltip, OverlayTrigger} from 'react-bootstrap';
import './../../resources/styles/style.css';
import './Report.css';
import Label from './../../components/Label.js';
import TextBox from './../../components/TextBox.js';
import {Animated} from "react-animated-css";
import Iframe from 'react-iframe';
import Header from './../../components/Header.js';
import Config from '../Config/Config.js';
import SmoothieComponent from 'react-smoothie';
import axios from 'axios';
import Chart from 'react-google-charts';
import Loader from 'react-loader-spinner';
import API from './../../Config/api.json';
import {PieChart} from 'react-easy-chart';

class Analytics extends Component {
  constructor(props) {
    super(props);

    this.state = {logLevel:{}, loader:false,
    showToolTip: false,
    top: 0,
    left: 0,
    value: '',
    key: ''};
  }

  componentDidMount() {
    let thisObj = this;

      var ts1 = this.refs.chart.addTimeSeries({
        strokeStyle: 'rgba(0, 255, 0, 1)',
        fillStyle: 'rgba(0, 255, 0, 0.1)',
        lineWidth: 1,
      });
      // var ts2 = this.refs.chart.addTimeSeries({
      //   strokeStyle: 'rgba(255, 0, 0, 1)',
      //   fillStyle: 'rgba(255, 0, 0, 0.2)',
      //   lineWidth: 4,
      // });
      this.dataGenerator = setInterval(function() {
        var time = new Date().getTime();
        // Generate times slightly in the future
        time += 1000;
        axios.get(API.current.apiUrl+'/api/v1/cpuinfo').then(function(res){
          console.log(res.data.cpu);

          ts1.append(time, res.data.cpu);
          //ts2.append(time, Math.random());
        }).catch(function(err){
          console.log(err);
        })

      }, 1000);

      // let pageObj = this;
      //
      // let inpData = {
      //   'endDate': (new Date()).toISOString(),
      //   'startDate': (new Date((new Date()).getTime() - (7 * 24 * 60 * 60 * 1000))).toISOString()
      // }
      //
      // axios.post(API.current.apiUrl+'/api/v1/elastic', inpData).then(function(res){
      //
      //       pageObj.setState({logLevel:res.data});
      //       console.log('res.data',res.data);
      //       console.log('this.state.logLevel.Emergency', pageObj.state.logLevel.Emergency);
      //
      // }).catch(function(err)
      // {
      //   console.log('err', err);
      // });
    }

  componentWillUnmount()
  {
    clearInterval(this.dataGenerator);
  }

    mouseOverHandler(d, e) {
        this.setState({
          showToolTip: true,
          top: e.y,
          left: e.x,
          value: d.value,
          key: d.data.key});
      }

      mouseMoveHandler(e) {
        if (this.state.showToolTip) {
          this.setState({top: e.y, left: e.x});
        }
      }

      mouseOutHandler() {
        this.setState({showToolTip: false});
      }

      createTooltip() {
        if (this.state.showToolTip) {
          return (
            <Tooltip
              top={this.state.top}
              left={this.state.left}
            >
              The value of {this.state.key} is {this.state.value}
            </Tooltip>
          );
        }
        return false;
      }

  render() {
    return (
        <div>
            <Grid>
                  <Row className="logStatesDiv">
                      {/*<Col xs={12} md={6}>
                          <Label text={"CPU States"} color="#000"></Label>
                      </Col>*/}

                      <Col xs={12} md={12}  style={{padding:'5px'}}>
                            <SmoothieComponent ref="chart" responsive={true} interpolation="smooth" minValue={0}  maxValue={100} strokeStyle="#ffffff"/>

                                <Label weight="bold" text={"CPU States"} color="#000" size="10px"></Label>
                      </Col>
                  </Row>

                  <Row className="logStatesDiv">
                      <Col xs={12} md={12}  style={{padding:'5px', textAlign:'center'}}>

                        <PieChart
                          size={150}
                          labels
                          styles={{
                            '.chart_lines': {
                              strokeWidth: 0
                            },
                            '.chart_text': {
                              fontFamily: 'serif',
                              fontSize: '1.25em',
                              fill: '#333'
                            }
                          }}

                          data={[
                            { key: 'A', value: 100, color: '#aaac84' },
                            { key: 'B', value: 200, color: '#dce7c5' },
                            { key: 'C', value: 50, color: '#e3a51a' }
                          ]}

                          innerHoleSize={200}
                          mouseOverHandler={this.mouseOverHandler}
                          mouseOutHandler={this.mouseOutHandler.bind(this)}
                          mouseMoveHandler={this.mouseMoveHandler.bind(this)}
                          padding={10}
                        />
                            {/*<Chart
                                width={'100%'}
                                height={'100%'}
                                chartType="PieChart"
                                loader={<div><Loader
                                   type="ThreeDots"
                                   color="#00BFFF"
                                   height="150"
                                   width="150"
                                /></div>}
                                data=
                                {[
                                      ['LogLevel', 'Count'],
                                      ['Emergency', 17],
                                      ['Alert', 29],
                                      ['Critical', 11],
                                      ['Error', 21],
                                      ['Warning', 92],
                                      ['Notice', 132],
                                      ['Informational', 371],
                                      ['Debug', 536]
                                  ]}
                                options={{
                                title: '',
                                is3D: true,
                                slices: {
                                          0: {color: '#ef0000'},
                                          1: {color: '#a94442'},
                                          2: {color: '#FFA500'},
                                          3: {color: '#ffa578'},
                                          4: {color: '#ff78a9'},
                                          5: {color: '#1911bf'},
                                          6: {color: '#10bf67'},
                                          7: {color: '#00e822'},
                                        }
                                }}
                                rootProps={{ 'data-testid': '1' }}
                                />*/}

                            <Label weight="bold" text={"Error States For Last 7 Days"} color="#000" size="10px"></Label>
                      </Col>
                  </Row>
            </Grid>
            {(this.state.loader)?
                <div className="fadeMe">
                    <div style={{position:'absolute', left:'45%', top: '40%'}}>
                      <Loader
                         type="ThreeDots"
                         color="#00BFFF"
                         height="150"
                         width="150"
                      />
                    </div>
                </div>:<div></div>
            }
        </div>
    );
  }
}

export default Analytics;
