import React, { Component } from 'react';
import {Button, FormGroup, FormControl, ControlLabel, Grid, Row, Col, Tooltip, OverlayTrigger} from 'react-bootstrap';
import './../../resources/styles/style.css';
import './Report.css';
import Label from './../../components/Label.js';
import TextBox from './../../components/TextBox.js';
import {Animated} from "react-animated-css";
import Iframe from 'react-iframe';
import Header from './../../components/Header.js';
import ShowReports from './ShowReports.js';
import Analytics from './Analytics.js';
import Textarea from 'react-textarea-autosize';
import axios from 'axios';
import Notifications, {notify} from 'react-notify-toast';
import Loader from 'react-loader-spinner';
import API from './../../Config/api.json';
import Dropdown from 'react-dropdown';
import 'react-dropdown/style.css';
import DonutChart from 'react-donut-chart';
import "react-awesome-popover/dest/react-awesome-popover.css";
import Popover from 'react-awesome-popover';

class SystemReport extends Component {
  constructor(props) {
    super(props);
    this.state ={}
    this.onDonutMouseEnter = this.onDonutMouseEnter.bind(this);
    this.onDountSliceClick = this.onDountSliceClick.bind(this);
  }

  onDountSliceClick(item, toggled)
  {
    console.log(item);
  }

  onDonutMouseEnter(item)
  {
    console.log(item);
  }


  componentDidMount()
  {
  }

  render() {
    return (
        <div className="manageShowReports" style={{color:'#000', width:'400px', height:'400px' }}>

        <DonutChart
          clickToggle={false}
          height={200}
          width = {200}
          legend = {false}
          onMouseEnter = {(item)=>this.onDonutMouseEnter(item)}
          onClick = {(item, toggled) => this.onDountSliceClick(item, toggled)}
          data={[{
              label: 'Emergency',
              value: 3,
              id:'1'
          },
          {
              label: 'Alert',
              value: 12
          },
          {
              label: 'Critical',
              value: 17
          },
          {
              label: 'Error',
              value: 18
          },
          {
              label: 'Warning',
              value: 9
          },
          {
              label: 'Notice',
              value: 11
          },
          {
            label:'Informational',
            value:20

          },
          {
            label:'Debug',
            value:5
          },
          {
              label: '',
              value: 5,
              isEmpty: true
          }]} />
        </div>
    );
  }
}

export default SystemReport;
