import React, { Component } from 'react';
import {Button, FormGroup, FormControl, ControlLabel, Grid, Row, Col, Tooltip, OverlayTrigger} from 'react-bootstrap';
import './../../resources/styles/style.css';
import './Report.css';
import Label from './../../components/Label.js';
import TextBox from './../../components/TextBox.js';
import {Animated} from "react-animated-css";
import Iframe from 'react-iframe';
import Header from './../../components/Header.js';
import Config from '../Config/Config.js';
import SmoothieComponent from 'react-smoothie';
import axios from 'axios';
import Chart from 'react-google-charts';

class Analytics extends Component {
  constructor(props) {
    this.state = {logLevel:{}}
    super(props);

  }

  componentDidMount() {
    let thisObj = this;

      var ts1 = this.refs.chart.addTimeSeries({
        strokeStyle: 'rgba(0, 255, 0, 1)',
        fillStyle: 'rgba(0, 255, 0, 0.1)',
        lineWidth: 1,
      });
      // var ts2 = this.refs.chart.addTimeSeries({
      //   strokeStyle: 'rgba(255, 0, 0, 1)',
      //   fillStyle: 'rgba(255, 0, 0, 0.2)',
      //   lineWidth: 4,
      // });
      this.dataGenerator = setInterval(function() {
        var time = new Date().getTime();
        // Generate times slightly in the future
        time += 1000;
        axios.get('http://localhost:3001/api/v1/cpuinfo').then(function(res){
          console.log(res.data.cpu);

          ts1.append(time, res.data.cpu);
          //ts2.append(time, Math.random());
        }).catch(function(err){
          console.log(err);
        })

      }, 1000);

      let pageObj = this;

      let inpData = {
        'startDate': (new Date()).toISOString(),
        'endDate': (new Date((new Date()).getTime() - (7 * 24 * 60 * 60 * 1000))).toISOString()
      }

      axios.post('http://localhost:3001/api/v1/elastic', inpData).then(function(res){
            this.setState({logLevel:res.data});
            console.log(this.state.logLevel.Error);
      }).catch(function(err)
      {
        console.log('err', err);
      });
    }

  componentWillUnmount()
  {
    clearInterval(this.dataGenerator);
  }

  render() {
    return (
        <div>
            <Grid>
                  <Row className="logStatesDiv">
                      {/*<Col xs={12} md={6}>
                          <Label text={"CPU States"} color="#000"></Label>
                      </Col>*/}

                      <Col xs={12} md={12}  style={{padding:'5px'}}>
                            <SmoothieComponent ref="chart" responsive={true} interpolation="smooth" minValue={0}  maxValue={100} strokeStyle="#ffffff"/>

                                <Label weight="bold" text={"CPU States"} color="#000" size="10px"></Label>
                      </Col>
                  </Row>

                  <Row className="logStatesDiv">
                      <Col xs={12} md={12}  style={{padding:'5px', textAlign:'center'}}>
                            <Chart
                                width={'100%'}
                                height={'100%'}
                                chartType="PieChart"
                                loader={<div>Loading</div>}
                                data={[
                                ['LogLevel', 'Count'],
                                ['Emergency', 911],
                                ['Alert', 20],
                                ['Critical', 20],
                                ['Error', 29],
                                ['Warning', 78],
                                ['Notice', 78],
                                ['Informational', 78],
                                ['Debug', 78]
                                ]}
                                options={{
                                title: '',
                                }}
                                rootProps={{ 'data-testid': '1' }}
                                />

                            <Label weight="bold" text={"Error States For Last 7 Days"} color="#000" size="10px"></Label>
                      </Col>
                  </Row>
            </Grid>
        </div>
    );
  }
}

export default Analytics;
