import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { Button, FormGroup, FormControl, ControlLabel,Grid,Row,Col } from 'react-bootstrap';
import '../resources/styles/meter.css';
import {Animated} from "react-animated-css";
import ReactSpeedometer from "react-d3-speedometer";
import Label from './Label.js';

const Meter = (props) => (
    <div className="meter">
      <Grid className="override_container">
          <Row className="show-grid">
            <Col xs={12} md={2}>
                <ReactSpeedometer
                    maxValue={500}
                    value={473}
                    needleColor="purple"
                    startColor="green"
                    segments={10}
                    endColor="red"
                    width={300}
                    height={300}
                    />
            </Col>
          </Row>
        </Grid>

        <Grid style={{width: "500px", height: "300px", background: "#EFEFEF"}}>
            <Row><Col><Label text={props.text} color={"#000"}></Label></Col></Row>
            <Row>
              <Col sm={12} lg={8}>

                </Col>

                <Col sm={12} lg={4}>
                    <Button variant="outline-primary">{props.text}</Button>
                </Col>
            </Row>
        </Grid>
    </div>
);

Meter.propTypes = {
    text: PropTypes.string
};

export default Meter;
