import React, { Component } from 'react';
import {Button, FormGroup, FormControl, ControlLabel, Grid, Row, Col, Tooltip, OverlayTrigger} from 'react-bootstrap';
import './../../resources/styles/style.css';
import './Report.css';
import Label from './../../components/Label.js';
import TextBox from './../../components/TextBox.js';
import {Animated} from "react-animated-css";
import Iframe from 'react-iframe';
import Header from './../../components/Header.js';
import Config from '../Config/Config.js';
import RGrid from './../../components/Grid.js';
import Modal from 'react-modal';
import Switch from "react-switch";
import Notifications, {notify} from 'react-notify-toast';
import axios from 'axios';
import * as JsSearch from 'js-search';
import AddReport from './../../components/AddReport.js';
import Datetime  from 'react-datetime';
//import './../../resources/styles/react-datetime.css';
import moment from 'moment';
import './../../../node_modules/react-datetime/css/react-datetime.css';

const toggleAdminFormatter = ({ value }) => {
  console.log(value);
  return <div>{}</div>
};

class ShowReports extends Component {

  constructor(props) {
    super(props);
    this.state =
    {
      modalIsOpen:false,
      startDate:null,
      endDate:null,
      columnsDef: [],
      rowRecords: []
    };

    this.geenrateReports = this.geenrateReports.bind(this);
    this.onDateChange = this.onDateChange.bind(this);
    this.onGenerateReportsClick = this.onGenerateReportsClick.bind(this);
    this.onCancelClick = this.onCancelClick.bind(this);

  }

  onCancelClick(event)
  {
    this.setState({modalIsOpen:false});
  }

  onGenerateReportsClick(event)
  {
    console.log(event);
  }

  onDateChange(event)
  {

  }

  geenrateReports(event)
  {
    this.setState({modalIsOpen:true});
  }

  bindReportGrid()
  {
    let gridColumns = [
      { key: 'id', name: 'User ID' },
      { key: 'first_name', name: 'First Name' },
      // { key: 'middle_name', name: 'Middle Name' },
      { key: 'last_name', name: 'Last Name' },
      { key: 'email_id', name: 'Email ID' },
      { key: 'contact_number', name:'Contact Number'},
      { key: 'is_admin', name: 'Admin' , formatter: toggleAdminFormatter},
      { key: 'emp_id', name: 'Employee ID' },
      { key: 'created_date', name: 'Create Date' },
      { key: 'is_active', name: 'Active' , formatter: toggleAdminFormatter}
    ];

    axios.get("http://127.0.0.1:3001/api/v1/elastic").then(function(res)
    {
        console.log(res);
    }).catch(function(err){
        console.log(err);
    })
  }

  componentDidMount()
  {

  }

  render() {
    return (
        <div className="manageShowReports" style={{color:'#000'}}>

          <div className="userStates">
              <Label text={"Daily Report(s) - 10 | Weekly Report(s) - 1 | Monthly Report(s) - 0"}></Label>
          </div>

          <div style={{display:(this.state.modalIsOpen) ? "none" : "" }}>
              <div className="users">
                <div className="addUsers"><AddReport text={"Generate Report"} onClickImplementation={this.geenrateReports}></AddReport></div>
                <div className="searchUsers"><TextBox inputType = "text" placeholder="Search" onChangeEvent={this.onChangeEvent}></TextBox><img src={require('./../../resources/images/search.png')} style={{width:'20px',height:'20px', cursor:'pointer'}} name="report"/></div>
              </div>
                <RGrid
                    columnDefs={this.state.columnsDef}
                    rowData = {this.state.rowRecords}
                    onGridRowsUpdated={this.onGridRowsUpdated}
                    getCellActions={this.getCellActions}
                  >
                </RGrid>
          </div>

          <div style={{display:(!this.state.modalIsOpen) ? "none" : "" }} className="users">
              <div className="createUser">
                  <Grid className="override_container">
                      <Row className="show-grid">
                        <Col xs={12} md={3}>
                          <Label text={"Start Date"} color={"#fff"} size={"12px"}></Label>
                        </Col>
                        <Col xs={12} md={3}>

                              <Datetime />

                        </Col>
                        <Col xs={12} md={3}>
                          <Label text={"End Date"} color={"#fff"} size={"12px"}></Label>
                        </Col>
                        <Col xs={12} md={3}>

                              <Datetime />

                        </Col>
                      </Row>

                      <Row className="show-grid">
                        <Col xs={12} md={3}>
                          <Button bsStyle="primary" className="button userPagebtn" bsSize="small" onClick={this.onGenerateReportsClick}>
                            Generate Report
                          </Button>
                          <Button bsStyle="primary" className="button userPagebtn" bsSize="small" onClick={this.onCancelClick}>
                            Cancel
                          </Button>
                        </Col>
                        <Col xs={12} md={3}>
                          <div></div>
                        </Col>
                        <Col xs={12} md={3}>
                          <div></div>
                        </Col>
                        <Col xs={12} md={3}>
                          <div></div>
                        </Col>
                      </Row>

                    </Grid>
                </div>
            </div>

            <Notifications options={{zIndex: 200, top: '50px'}} />

        </div>
    );
  }
}

export default ShowReports;
