import React, { Component } from 'react';
import { Button, FormGroup, FormControl, ControlLabel,Grid,Row,Col } from 'react-bootstrap';
import './resources/styles/landing.css';
import {Animated} from "react-animated-css";
import TextBox from './components/TextBox.js';
import Label from './components/Label.js';
import Notifications, {notify} from 'react-notify-toast';

class Landing extends Component {

  constructor(props) {
    super(props);
    this.state = {userName:'',password:''};
    this.onChangeEvent = this.onChangeEvent.bind(this);
    this.onLoginButtonClick = this.onLoginButtonClick.bind(this);
  }

  onLoginButtonClick(event)
  {
      if((this.state.userName === "")&&(this.state.password === ""))
      {
        notify.show("Please enter User Name and Password.", "error", 2000);
      }
      else
      {
        notify.show("User Name or Password is wrong.", "error", 2000);
      }

    //this.props.history.push('/dashboard')
    // if((this.state.userName === "admin")&&(this.state.password === "admin"))
    // {
    //   this.props.history.push('/dashboard')
    // }
    // else {
    //   if((this.state.userName === "")&&(this.state.password === ""))
    //   {
    //     notify.show("Please enter User Name and Password.", "error", 2000);
    //   }
    //   else
    //   {
    //     notify.show("User Name or Password is wrong.", "error", 2000);
    //   }
    // }
  }

  onChangeEvent(event)
  {
      if(event.target.name === "username")
      {
        this.setState({ userName: event.target.value},() => console.log(this.state));
      }
      else {
        this.setState({ password: event.target.value},() => console.log(this.state));
      }
  }

  render() {
    return (
        <div className="login" >
          <Grid>
            <Row>
              <Row className="show-grid">
                <Col xs={6} md={6}>
                  <Grid className="formGrid">
                    <Row className="show-grid">
                      <Col xs={12} md={12}>
                        <Label text="User Name"/>
                      </Col>
                      <Col xs={12} md={12}>
                        <TextBox onChangeEvent={this.onChangeEvent} name="username" inputType="text"/>
                      </Col>
                    </Row>
                    <Row className="show-grid">
                      <Col xs={12} md={12}>
                        <Label text="Password"/>
                      </Col>
                      <Col xs={12} md={12}>
                        <TextBox onChangeEvent={this.onChangeEvent} name="password" inputType="password"/>
                      </Col>
                    </Row>
                    <Row className="show-grid">
                      <Col xs={12} md={12} >
                          <Button bsStyle="primary" className="button" bsSize="small" onClick={this.onLoginButtonClick}>
                            Login
                          </Button>
                      </Col>
                      <Col xs={12} md={12}>
                          <Label text="Forgot Password"/>
                      </Col>
                    </Row>

                  </Grid>
                </Col>
                <Col xs={6} md={6} className="verticalLine">
                  <div className="logo-holder">
                    <div className="logo"></div>
                  </div>
                </Col>
              </Row>
            </Row>
          </Grid>
          <Notifications options={{zIndex: 200, top: '50px'}} />
        </div>
    );
  }
}

export default Landing;
