import React, { Component } from 'react';
import {Button, FormGroup, FormControl, ControlLabel, Grid, Row, Col, Tooltip, OverlayTrigger} from 'react-bootstrap';
import './../../resources/styles/style.css';
import './Report.css';
import Label from './../../components/Label.js';
import TextBox from './../../components/TextBox.js';
import {Animated} from "react-animated-css";
import Iframe from 'react-iframe';
import Header from './../../components/Header.js';
import Config from '../Config/Config.js';
import SmoothieComponent from 'react-smoothie';
import axios from 'axios';
import Chart from 'react-google-charts';
import Loader from 'react-loader-spinner';
import API from './../../Config/api.json';
// import {PieChart} from 'react-easy-chart';
import {PieChart, BarChart, Legend, ResponsiveContainer} from 'react-easy-chart';
import ReactSvgPieChart from "react-svg-piechart";
import Meter from '../../components/meter.js';
import Datetime  from 'react-datetime';
import Modal from 'react-responsive-modal';
import Pagination from "react-js-pagination";

class Analytics extends Component {
  constructor(props) {
    super(props);

    var d = (new Date());

    this.state = {
      loader:false,
      sDT:(new Date((d.setHours(d.getHours()-1*24*90)))),
      eDT:(new Date()),
      logInformation:[]};

      this.onApplyTimeClick = this.onApplyTimeClick.bind(this);
  }


  componentDidMount() {
    var d = (new Date());
    let pageObj = this;
    this.setState({sDT : "2018-01-01T07:25:48.192Z", eDT : "2019-03-07T07:25:48.192Z"},()=>{
        this.bindLogLevelGuage();
    })
  }

  onApplyTimeClick()
  {
      this.bindLogLevelGuage();
  }

  bindLogLevelGuage()
  {
    let pageObj = this;
    axios.post(API.current.apiUrl+"/api/v1/analytics",this.state).then(function(res)
    {
      pageObj.setState({logInformation:res.data.data});
    }).catch(function(err){
        console.log(err);
    })
  }


  render() {
    return (
        <div style={{'height':'85vh', 'overflowY':'scroll'}}>
          <Grid className="override_container">
              <Row className="show-grid">
                <Col xs={12} md={2}>
                  <Label text={"Start Date"} color={"#000"} size={"12px"}></Label>
                </Col>
                <Col xs={12} md={3}>
                      <Datetime className="datePickerField" value={this.state.startDateTime} onChange={this.onStartDateChange}/>
                </Col>
                <Col xs={12} md={2}>
                  <Label text={"End Date"} color={"#000"} size={"12px"}></Label>
                </Col>
                <Col xs={12} md={3}>
                      <Datetime className="datePickerField" value={this.state.endDateTime} defaultValue={new Date()} onChange={this.onEndDateChange}/>
                </Col>
                <Col xs={12} md={2}>
                  <Button bsStyle="primary" className="button userPagebtn" bsSize="small" onClick={this.onApplyTimeClick}>
                    Apply
                  </Button>
                </Col>
              </Row>
            </Grid>

            <div>
              <div>
                    {this.state.logInformation.map((value, index) => {
                      console.log(value.totalPackets,index);
                      return <Meter totalPackets={value.totalPackets} logLevelDescription={value.logLevelDescription} packetsForLogLevel={value.packetsForLogLevel} key={index}
                        topApplicances={value.topApplicances.SYSLOG_HOSTNAME.buckets} logLevel={value.logLevel} onApplicanceClick={this.onApplicanceClick}></Meter>
                    })}
              </div>
            </div>
        </div>
    );
  }
}

export default Analytics;
