import React, { Component } from 'react';
import {Button, FormGroup, FormControl, ControlLabel, Grid, Row, Col, Tooltip, OverlayTrigger} from 'react-bootstrap';
import './../../resources/styles/style.css';
import './Users.css';
import Label from './../../components/Label.js';
import TextBox from './../../components/TextBox.js';
import RButton from './../../components/Button.js';
import {Animated} from "react-animated-css";
import Iframe from 'react-iframe';
import Header from './../../components/Header.js';
import RGrid from './../../components/Grid.js';
import Modal from 'react-modal';
import Switch from "react-switch";
import Notifications, {notify} from 'react-notify-toast';
import axios from 'axios';

const customStyles = {
  content : {
    top                   : 'auto',
    left                  : 'auto',
    right                 : 'auto',
    bottom                : 'auto',
    transform             : 'translate(-50%, -50%)',
    border                : '1px solid yellow'
  }
};

const toggleAdminFormatter = ({ value }) => {
  console.log(value);
  return <div>{(value == "1") ? <Label text="Yes" size="16px" color="green" weight="light"></Label>:<Label  weight="light" text="No" size="16px" color="red"></Label>}</div>
};



class Users extends Component {
  constructor(props) {
    super(props);

    this.state = {
      modalIsOpen: false,
      firstName:'',
      lastName:'',
      emailId : '',
      contactNumber:'',
      employeeId:'',
      password:'',
      confirmPassword:'',
      isAdmin:false,
      columnsDef:[],
      rowRecords:[]
    };

    this.openModal = this.openModal.bind(this);
    this.afterOpenModal = this.afterOpenModal.bind(this);
    this.closeModal = this.closeModal.bind(this);
    this.addUser = this.addUser.bind(this);
    this.adduserToDB = this.adduserToDB.bind(this);
    this.onExit = this.onExit.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.changeTaxtchange = this.changeTaxtchange.bind(this);
    //this.onGridRowsUpdated = this.onGridRowsUpdated.bind(this);
  }

  

  onGridRowsUpdated = ({ fromRow, toRow, updated }) =>{
    console.log('fromRow', fromRow);
    console.log('toRow', toRow);
    console.log('updated', updated);
  }

  getUserDetails()
  {
    let pageObj = this;
    let gridColumns = [

      { key: 'id', name: 'User ID' },
      { key: 'first_name', name: 'First Name' },
      // { key: 'middle_name', name: 'Middle Name' },
      { key: 'last_name', name: 'Last Name' },
      { key: 'email_id', name: 'Email ID' },
      { key: 'contact_number', name:'Contact Number'},
      { key: 'is_admin', name: 'Admin' , formatter: toggleAdminFormatter},
      { key: 'emp_id', name: 'Employee ID' },
      { key: 'created_date', name: 'Create Date' }
    ];

    this.setState({columnsDef:gridColumns});
    axios.get('http://localhost:3001/api/v1/user').then(function(res){
      console.log('res', res.data);
      pageObj.setState({rowRecords: res.data});
    }).catch(function(err){
      console.log('error');
    })
  }
  componentDidMount()
  {
    this.getUserDetails();
  }

  changeTaxtchange(event)
  {
    let name = event.target.name;
    this.setState({ [name] : event.target.value});
  }

  handleChange(event)
  {
    this.setState({isAdmin:event});
  }

  onExit(event)
  {

    this.setState({modalIsOpen:false});
  }

  adduserToDB(event)
  {
    let pageObj = this;
    if(this.state.firstName.trim() == '')
    {
      notify.show("Please enter first name.", "error", 2000);
      return false;
    }else if (this.state.lastName.trim() == '') {
      notify.show("Please enter last name.", "error", 2000);
      return false;
    }else if (this.state.emailId.trim() == '') {
      notify.show("Please enter email ID.", "error", 2000);
      return false;
    }
    else if (!this.state.emailId.match(/^([a-zA-Z0-9_\-\.]+)@([a-zA-Z0-9_\-\.]+)\.([a-zA-Z]{1,10})$/)) {
      notify.show("Please enter valid email ID.", "error", 2000);
      return false;
    }else if (this.state.contactNumber.trim() == '') {
      notify.show("Please enter contact number.", "error", 2000);
      return false;
    }else if (this.state.employeeId.trim() == '') {
      notify.show("Please enter employee ID.", "error", 2000);
      return false;
    }else if (this.state.password.trim() == '') {
      notify.show("Please enter password.", "error", 2000);
      return false;
    }else if (this.state.confirmPassword.trim() == '') {
      notify.show("Please confirm your password.", "error", 2000);
      return false;
    }else if (this.state.password.trim() != this.state.confirmPassword.trim()) {
      notify.show("Password does not match.", "error", 2000);
      return false;
    }


    axios.post('http://localhost:3001/api/v1/user',this.state).then(function(res){
      console.log('res', res.data);
      if(res.data.alreadyExists)
      {
        //user already exists
        notify.show("User already exists.", "error", 2000);
      }
      else if (res.data.createdSuccessFully) {
        //created successfully
        notify.show("User added successfully.", "success", 2000);
        pageObj.getUserDetails();
      }
      else {
        //system error
        notify.show("System error, Please try after some time.", "error", 2000);
      }
    }).catch(function(error) {
      console.log('error',error);
    })

    //set state to Initial state
    this.setState({modalIsOpen: false,
    firstName:'',
    lastName:'',
    emailId : '',
    contactNumber:'',
    employeeId:'',
    password:'',
    confirmPassword:'',
    isAdmin:false});
  }

  addUser(event)
  {

    this.setState({modalIsOpen: true});
    console.log(event);
  }

  openModal() {
    this.setState({modalIsOpen: true});
  }

  afterOpenModal() {
    // references are now sync'd and can be accessed.
    //this.subtitle.style.color = '#f00';https://www.youtube.com/watch?v=MTsFmUI54as
  }

  closeModal() {
    this.setState({modalIsOpen: false});
  }

  updateUserinfo(row)
  {
      console.log(row);
  }

  getCellActions=(column, row)=>{
      console.log('column', column);
      console.log('row', row);

      if (column.key === 'id') {
          return [
                {
                  icon: "glyphicon glyphicon-edit",
                  callback: () => {
                    this.updateUserinfo(row);
                  }
                }
          ]
      }
  }

  render() {

    return (
        <div className="manageUsers">
          <div className="userStates">
              <Label text="Total Users- 100&nbsp;&nbsp;|&nbsp;&nbsp;Active Users- 88"></Label>
          </div>
          <div className="users">
            <div className="addUsers"><RButton text={"Add User"} onClickImplementation={this.addUser}></RButton></div>
            <div className="searchUsers"><TextBox inputType = "text" placeholder="Search"></TextBox><img src={require('./../../resources/images/search.png')} style={{width:'20px',height:'20px', cursor:'pointer'}} name="report"/></div>
          </div>
          <RGrid
            columnDefs={this.state.columnsDef}
            rowData = {this.state.rowRecords}
            onGridRowsUpdated={this.onGridRowsUpdated}
            getCellActions={this.getCellActions}
              >
          </RGrid>
          {/*
            <div className="users">
              <Label text={"Active Users : 100, In-Active Users : 50"}></Label>
            </div>

            className={{ base: [style.base]}}
            overlayClassName={{ base: [style.overlayBase] }}
            style={customStyles}
            */}
            <Modal
              isOpen = {this.state.modalIsOpen}
              onAfterOpen = {this.afterOpenModal}
              onRequestClose = {this.closeModal}
              className = "Modal"
              overlayClassName = "Overlay"
              ariaHideApp = {false}
            >
            <div className="createUser">

              <div className ="flexRow">
                  <div className="flexColumn">
                      <Label text={"First Name"} color={"#fff"} size={"12px"}></Label>
                  </div>
                  <div className="flexColumn">
                      <TextBox inputType="text" width={"250px"} name="firstName" onChangeEvent={this.changeTaxtchange}></TextBox>
                  </div>
              </div>
              <div className ="flexRow">
                  <div className="flexColumn">
                      <Label text={"Last Name"} color={"#fff"} size={"12px"}></Label>
                  </div>
                  <div className="flexColumn">
                      <TextBox inputType="text" width={"250px"} name="lastName" onChangeEvent={this.changeTaxtchange}></TextBox>
                  </div>
              </div>
              <div className ="flexRow">
                  <div className="flexColumn">
                      <Label text={"Email ID"} color={"#fff"} size={"12px"}></Label>
                  </div>
                  <div className="flexColumn">
                      <TextBox inputType="text" width={"250px"} name="emailId" onChangeEvent={this.changeTaxtchange}></TextBox>
                  </div>
              </div>
              <div className ="flexRow">
                  <div className="flexColumn">
                      <Label text={"Contact Number"} color={"#fff"} size={"12px"}></Label>
                  </div>
                  <div className="flexColumn">
                      <TextBox inputType="text" width={"250px"} name="contactNumber" onChangeEvent={this.changeTaxtchange}></TextBox>
                  </div>
              </div>
              <div className ="flexRow">
                  <div className="flexColumn">
                      <Label text={"Employee ID"} color={"#fff"} size={"12px"}></Label>
                  </div>
                  <div className="flexColumn">
                      <TextBox inputType="text" width={"250px"} name="employeeId" onChangeEvent={this.changeTaxtchange}></TextBox>
                  </div>
              </div>
              <div className ="flexRow">
                  <div className="flexColumn">
                      <Label text={"Password"} color={"#fff"} size={"12px"}></Label>
                  </div>
                  <div className="flexColumn">
                      <TextBox inputType="password" width={"250px"} name="password" onChangeEvent={this.changeTaxtchange}></TextBox>
                  </div>
              </div>
              <div className ="flexRow">
                  <div className="flexColumn">
                      <Label text={"Confirm Password"} color={"#fff"} size={"12px"}></Label>
                  </div>
                  <div className="flexColumn">
                      <TextBox inputType="password" width={"250px"} name="confirmPassword" onChangeEvent={this.changeTaxtchange}></TextBox>
                  </div>
              </div>
              <div className ="flexRow">
                  <div className="flexColumn">
                      <Label text={"Admin"} color={"#fff"} size={"12px"}></Label>
                  </div>
                  <div className="flexColumn" style={{width:'55%'}}>
                      <Switch
                        onChange={this.handleChange}
                        checked={this.state.isAdmin}
                        id="normal-switch"
                        onColor = "#33cc33"
                        offColor = "#ff0000"
                      />
                  </div>
              </div>
              <div className ="flexRow submitBtn">
                  <div className="flexColumn">
                    <OverlayTrigger placement="left" overlay={<Tooltip id="tooltip">Save</Tooltip>}>
                      <img src={require('./../../resources/images/enter.png')} onClick={this.adduserToDB} name="submit" className="logoutBtn"/>
                    </OverlayTrigger>
                  </div>
                  <div className="flexColumn">
                    <OverlayTrigger placement="left" overlay={<Tooltip id="tooltip">Cancel</Tooltip>}>
                        <img src={require('./../../resources/images/cancel.png')} name="cancel" onClick={this.onExit} className="logoutBtn"/>
                    </OverlayTrigger>
                  </div>
              </div>
              </div>
            </Modal>
            <Notifications options={{zIndex: 200, top: '50px'}} />
        </div>
    );
  }
}

export default Users;
